package hashing;

import java.util.HashMap;

/*Given a string, 
find the length of the longest substring without repeating characters.*/



/* This condition is the catch here .....  hm.get(a.charAt(i)).intValue() >= start
 * 
 * This prevents checking the previous occurrence of the character other than the current substring*/
public class LongestSubstringNonRepeatingChars {

	

	public int lengthOfLongestSubstring(String a) {
		HashMap<Character, Integer> hm = new HashMap<Character, Integer>();
		
		
		int length = a.length();
		int maxLength = 0;
		int currentLength =0;
		int start = 0;
		
		for (int i = 0; i < length; i++) {
			if(!hm.containsKey(a.charAt(i)) || hm.get(a.charAt(i)).intValue() < start){
				
				hm.put(a.charAt(i), i);
				currentLength++;
				
			}else{
			
				if(currentLength>maxLength){
					maxLength = currentLength;
					
				}
				
				currentLength = i-hm.get(a.charAt(i)).intValue();
				start = hm.get(a.charAt(i)).intValue()+1;
				hm.put(a.charAt(i), i);
			
			}
			
		}
if(currentLength>maxLength)
	maxLength = currentLength;
		return maxLength;

	}



	public static void main(String[] args) {

		LongestSubstringNonRepeatingChars obj = new LongestSubstringNonRepeatingChars();
		System.out.println(obj.lengthOfLongestSubstring(
				""));

	}

}



